/**
 * Apply Latin ligature feature to a range of tokens
 */
import { ContextParams } from '../../tokenizer';
import applySubstitution from '../applySubstitution';
/**
 * Update context params
 * @param {any} tokens a list of tokens
 * @param {number} index current item index
 */

function getContextParams(tokens, index) {
  var context = tokens.map(function (token) {
    return token.activeState.value;
  });
  return new ContextParams(context, index || 0);
}
/**
 * Apply Arabic required ligatures to a context range
 * @param {ContextRange} range a range of tokens
 */


function latinLigature(range) {
  var _this = this;

  var script = 'latn';
  var tokens = this.tokenizer.getRangeTokens(range);
  var contextParams = getContextParams(tokens);
  contextParams.context.forEach(function (glyphIndex, index) {
    contextParams.setCurrentIndex(index);

    var substitutions = _this.query.lookupFeature({
      tag: 'liga',
      script: script,
      contextParams: contextParams
    });

    if (substitutions.length) {
      substitutions.forEach(function (action) {
        return applySubstitution(action, tokens, index);
      });
      contextParams = getContextParams(tokens);
    }
  });
}

export default latinLigature;
